const works=document.querySelector('.works');if(works){const prevButton=document.querySelector('.column_left');const nextButton=document.querySelector('.column_right');const work=document.querySelectorAll(".work");var length=works.getAttribute("id")-1;var nowIndex=0;work[nowIndex].classList.add("show");TweenMax.fromTo(".bar",5,{width:"0%",ease:"Linear"},{width:"100%",ease:"Linear"});var getColor=work[nowIndex].getAttribute("id");document.body.style.backgroundColor=getColor;function sliderSlide(val){TweenMax.fromTo(".bar",5,{width:"0%",ease:"Linear"},{width:"100%",ease:"Linear"});prevButton.disabled=true;nextButton.disabled=true;work[nowIndex].classList.remove("show");nowIndex=val;work[nowIndex].classList.add("show");getColor=work[nowIndex].getAttribute("id");document.body.style.backgroundColor=getColor;}
function slidePrev(){const itemToMove=works.removeChild(works.lastElementChild);works.prepend(itemToMove);var index=nowIndex-1;if(index<0){index=length;}
sliderSlide(index);works.classList.add('sliding-right');}
function slideNext(){const itemToMove=works.removeChild(works.firstElementChild);works.append(itemToMove);var index=nowIndex+1;if(index>length){index=0;}
sliderSlide(index);works.classList.add('sliding-left');}
prevButton.addEventListener("click",slidePrev,false);nextButton.addEventListener("click",slideNext,false);setInterval(slideNext,5000);works.addEventListener('animationend',()=>{nextButton.disabled=false;prevButton.disabled=false;works.classList.remove('sliding-left','sliding-right');})}
var about=document.querySelector(".icon-about");var close=document.querySelector(".icon-close");var modal=document.querySelector(".modal");about.addEventListener("click",function(){modal.classList.add("show");});close.addEventListener("click",function(){modal.classList.remove("show");});var nameDivs=document.querySelectorAll(".name");if(nameDivs){nameDivs.forEach(function(nameDiv){var xhr=new XMLHttpRequest();xhr.addEventListener("load",function(){nameDiv.innerHTML=xhr.responseText;});xhr.open("GET",nameDiv.getAttribute("data-src"));xhr.send();});}